var toggleMenu=document.querySelector(".toggle-menu"),sidenav=document.querySelector(".side-nav"),close=document.querySelector(".close"),main=document.querySelector("main");toggleMenu.addEventListener("click",function(){main.classList.add("move-right"),sidenav.classList.add("open")}),close.addEventListener("click",function(){main.classList.remove("move-right"),sidenav.classList.remove("open")});